css.music-player:
#tile-[tile_id] .tile-primary i.material-icons {
    visibility: hidden;
    width: 1em;
    height: 1em;
    overflow: hidden;
}

#tile-[tile_id] .tile-primary i.material-icons.[class]:before {
    content: "\[value]";
    font-family: "Material Design Icons" !important;
    visibility: visible;
}

css.dashboard:
#tile-[tile_id] {
    height: 100%;
}

#tile-[tile_id] .tile-primary {
    line-height: 1.2em;
}

#tile-[tile_id] .tile-primary:before {
    display: block;
    line-height: 1.2em;
    font-size: 40px;
    visibility: visible;
}

#tile-[tile_id] .tile-primary:after {
    font-size: 18px;
    margin-left: 0px;
    line-height: 1.2em;
     display: none; 
}

tm.css:
#tile-[tile_id] {
   border: 0px none;
}

#tile-[tile_id] .tile-contents {
   height: calc(100%);
}

#tile-[tile_id] .tile-contents,
#tile-[tile_id] .tile-primary,
#tile-[tile_id] .tile-primary>div {
   margin: 0;
   padding: 0;
}

#tile-[tile_id] .tile-title {
   position: absolute;
   z-index: 9;
   padding: .25em;
   width: 100%;
   text-align: center;
   opacity: .5;
   font-weight: normal;
   font-size: .9em;
}

